import React from "react";
import { Link } from "react-router-dom";

let getTitle = (note) => {
  let title = note.body.split('\n')[0]
  let finalTitle
  if(title.length > 45) {
    
    finalTitle = title.slice(0,45)
    return 
    
  }

 

};
const ListItem = ({ note }) => {
  return (
    <Link to={`/note/${note.id}`}>
      <div className="notes-list-item">
        <h3>{getTitle(note)}</h3>
      </div>
    </Link>
  );
};

export default ListItem;
