import React, { Fragment, useContext, useState } from "react";
import { useDispatch } from "react-redux";
import { darkMode, lightMode } from "../actions/index";
import { useToasts } from "react-toast-notifications";
import { Link } from "react-router-dom";
import AuthContext from "../context/AuthContext";

const Navbar = (props) => {
  const [toggle, setToggle] = useState(false);
  const dispatched = useDispatch();
  const { addToast } = useToasts();
  const { user } = useContext(AuthContext);

  const triggerToggle = () => {
    setToggle(!toggle);
    toggleMode(toggle);
  };

  let toggleMode = (mode) => {
    if (mode === false) {
      dispatched(darkMode());
      addToast("Dark Mode enabled ðŸ¥³", {
        appearance: "success",
        autoDismiss: true,
      });
    } else {
      dispatched(lightMode());
      addToast("Dark Mode disabled ðŸ¥³", {
        appearance: "success",
        autoDismiss: true,
      });
    }
  };

  return (
    <div className="navbar">
      <h1>
        <Link to={"/"}>My Notes</Link>
      </h1>

      {/* dark mode button start here */}
      <div className="navbar-buttons">
        <div
          onClick={triggerToggle}
          className={`wrg-toggle ${toggle ? "wrg-toggle--checked" : ""}`}
        >
          <div className="wrg-toggle-container">
            <div className="wrg-toggle-check">
              <span>ðŸŒœ</span>
            </div>

            <div className="wrg-toggle-uncheck">
              <span>ðŸŒž</span>
            </div>
          </div>
          <div className="wrg-toggle-circle"></div>
          <input
            className="wrg-toggle-input"
            type="checkbox"
            aria-label="Toggle Button"
          />
        </div>

        {user ? (
          <div className="auth-button">
            <h3>Hello, {user.name}</h3>
            <h3><Link to={'/'}>Logout</Link></h3>
          </div>
        ) : (
          <div className="auth-button">
            <h3>
              <Link to={"/accounts"}>Login/Register</Link>
            </h3>
          </div>
        )}
      </div>
    </div>
  );
};

export default Navbar;
