import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const NotePage = () => {
  const { id } = useParams();
  const [note, setNote] = useState(null);

  useEffect(() => {
    getNote();
  }, );

  const getNote = async () => {
    const response = await fetch(`/api/notes/${id}`);
    const data = response.json();
    setNote(data);
  };

  return (
    <div>
      <h3>{note.body}</h3>
    </div>
  );
};

export default NotePage;
