import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useToasts } from "react-toast-notifications";

const INITIAL_DATA = {
  name: "",
  email: "",
  password: "",
};

const RegisterPage = () => {
  const [user, setUser] = useState(INITIAL_DATA);
  const { addToast } = useToasts();
  const history = useNavigate();

  const onSubmitHandeler = async (e) => {
    e.preventDefault();
    const response = await fetch("/api/register/", {
      method: "POST",
      headers: {
        Authorization: localStorage.getItem("authToken")
          ? "JWT " + localStorage.getItem("authToken")
          : null,
        "Content-Type": "application/json",
        accept: "application/json",
      },
      body: JSON.stringify({
        name: user.name,
        email: user.email,
        password: user.password,
      }),
    });
    // const data = await response.json()
    if(response.status === 200){
      setUser(INITIAL_DATA)
      addToast(`Registration Successfull ðŸ¥³`, {
        appearance: "success",
        autoDismiss: true,
      });
      history("/");
    }else{
      addToast(`Registration Successfull ðŸ¥³`, {
        appearance: "success",
        autoDismiss: true,
      });
    }
    console.log("form submited");
  };

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  return (
    <div className="loginForm-container">
      <span id="formTitle">Register</span>
      <form className="loginForm" onSubmit={(e) => onSubmitHandeler(e)}>
        <input
          type="text"
          name="name"
          className="loginInput"
          onChange={handleChange}
          placeholder="Enter name "
        />
        <input
          type="email"
          name="email"
          className="loginInput"
          onChange={handleChange}
          placeholder="Enter email "
        />
        <input
          type="password"
          name="password"
          className="loginInput"
          onChange={handleChange}
          placeholder="Enter password "
        />
        <button className="loginInput" id="login-btn" type="submit">
          Register
        </button>
      </form>
      <p>
        Aldready have an account?<Link to={"/login"}> Login</Link>
      </p>
    </div>
  );
};

export default RegisterPage;
