const defaultMode = false;

const toggleMode = (state = defaultMode, action) => {
  switch (action.type) {
    case "DARKMODE":
      return (state = true);
    case "LIGHTMODE":
      return (state = false);
    default:
      return state;
  }
};

export default toggleMode;
