const defaultMode = false;

const toggleMode = (state = defaultMode, action) => {
  // switch (action.type) {
  //   case "DARKMODE":
  //     return (state = true);
  //   case "LIGHTMODE":
  //     return (state = false);
  //   default:
  //     return state;
  // }

  if(action.type === "DARKMODE"){
    return state = true
  }else if(action.type === "LIGHTMODE"){
    return state = false
  }else{
    return state
  }
};

export default toggleMode;
